function beep() {
    var snd = new Audio("data:audio/wav;base64,");  
    snd.play();
}
$(document).ready(function() {
	
	window.Proceso = new (function() {
		//Constructor de proceso
		function Proceso() {}
	
		// Metodos publicos para Proceso
		Proceso.prototype = {
			data : {
				maestros : './data.maestro.aspx',
				laboratorios : './data.laboratorios.aspx'
			},
			actualizaMaestro : function(clave){
				var self = this;
				var url_maestros = self.data.maestros + "?clave=" + clave;
				// if(clave == self.claveMaestro) return false; 

				$.getJSON(url_maestros,function(maestro){

					if(maestro.Nombre){
						self.claveMaestro = maestro.Clave;
						$('#field_clave').empty().text(maestro.Clave);
						$('#field_nombre').empty().text(maestro.Nombre);
						$('#field_apellido').empty().text(maestro.Apellido);
						self.habilitarSeleccion();
						self.habilitaLiberar();
					}else{
						$('#field_clave').empty().text("No se encuentra en BD");
						$('#field_nombre').empty().text("-");
						$('#field_apellido').empty().text("-");
					}
				});

			},

			habilitarSeleccion : function(){
				$("#lista-laboratorios>ul>li>button.asignar").each(function(){
					// if(state ===false){
					// 	this.disabled = true;
					// }else 
					if($(this).prev().attr('data-status') === "0"){
						this.disabled = false;
					}else{
						this.disabled = true;
					}
				});
			},


			habilitaLiberar : function(){
				$("#lista-laboratorios>ul>li>button.liberar").each(function(){

					if($(this).prevAll(".icon-status").attr('data-status') === "1"){
						this.disabled = false ;
					}else{
						this.disabled = true;
					}
				});

			},

			limpiarMaestro : function(){

				$('#field_clave').empty().text("");
				$('#field_nombre').empty().text("");
				$('#field_apellido').empty().text("");
				this.claveMaestro="00000" ;

			},

			asignaLab : function(idlab){
				var confirmacion = confirm("¿Desea asignar este laboratorio?");
				if(confirmacion == false ) return false;

				var self = this;
				var url = self.data.laboratorios + "?accion=asigna&idlab="+idlab+"&idmaestro="+this.claveMaestro;
				$.get(url, function(resultado){
					$('#lista-laboratorios').empty().html(resultado);
				});
				this.limpiarMaestro();
				//this.habilitaLiberar();

			},

			liberaLab : function(idlab){
				var confirmacion = confirm("¿Desea liberar este laboratorio?");
				if(confirmacion == false ) return false;

				var self = this;
				var url = self.data.laboratorios + "?accion=libera&idlab="+idlab;
				$.get(url, function(resultado){
					$('#lista-laboratorios').empty().html(resultado);
				});
				this.limpiarMaestro();
			},


			actualizaLabs :function(){
				var self = this;
				$.ajax({
					url: self.data.laboratorios
				})
				.done(function(data) {
					$('#lista-laboratorios').html(data);
					self.habilitaLiberar();
				});
			}
		};
			return Proceso;
	}());


	Proceso.actualizaLabs();
	




	var Qr = {
		on_read : function(data){
			Proceso.actualizaMaestro(data);
			beep();
		},
		on_error : function(){
			
		},
		on_vide_error : function(){
			alert("Camara no instalada");
		}
	}



	$("#qr-reader").html5_qrcode(Qr.on_read,Qr.on_error,Qr.on_vide_error);

	// $('#lista-laboratorios>ul>li>button').on( 'click', asignar ));

	// $('#reader').html5_qrcode(function(data) {
	// 		$('#<%=txtCodigo.ClientID%>').val(data);
	// 	},
	// 	function(error) {},
	// 	function(videoError) {
	// 		alert("No hay cámara");
	// 	}
	// );
});